import { State, AnyAction, DerivedAction, ActionTypes, Selectors, ActionCreators } from './interfaces';
import { ModelSchemaReader } from './schema';
export default class Derivator<S extends State> {
    actionTypes: ActionTypes;
    actionCreators: ActionCreators<S>;
    schema: ModelSchemaReader;
    selectors: Selectors<S>;
    constructor(actionTypes: ActionTypes, actionCreators: ActionCreators<S>, schema: ModelSchemaReader, selectors: Selectors<S>);
    deriveAction(state: S, action: AnyAction): DerivedAction | AnyAction;
    private deriveDetachActions;
    private deriveAttachActions;
    private deriveDeleteActions;
    private detachOccupant;
}
